 tvMarca.setText(vehiculo.getMarca());
 tvModelo.setText(vehiculo.getModelo());
 tvAnioFabricacion.setText(String.valueOf(vehiculo.getAnioFabricacion()));
 tvPrecioBase.setText(String.valueOf(vehiculo.getPrecioBase()));

